import uniqid from 'uniqid'
import { projects } from '../../portfolio'
import ProjectContainer from '../ProjectContainer/ProjectContainer'
import './Projects.css'
import useFetch from '../../hooks/useFetch'
import { Skeleton } from '@mui/material';

const Projects = () => {

  const { REACT_APP_dataSourceURL: dataSourceURL } = process.env
  const { data: projectsData, error, loading } = useFetch(`${dataSourceURL}/api/projects?populate=deep`)

  console.log("projectData", projectsData)
  console.log("error", error)
  console.log("loading", loading)

  // if (projectsData === null) return null
  // if (projectsData.data.length === 0) return null

  return (
    <section id='projects' className='section projects'>
      <h2 className='section__title'>Projects</h2>

      <div className='projects__grid'>
        {loading === true
          ? <>
            <Skeleton variant="text" sx={{ fontSize: '1rem' }} />
            <Skeleton variant="rectangular" width={210} height={118} />
          </>
          : projectsData.data.map((project) => (
            <ProjectContainer key={uniqid()} project={project} />
          ))}
      </div>
    </section>
  )
}

export default Projects
